={{ (() => {
  const rawJid = ($('Webhook').item.json.body.data.key.remoteJid || '').trim();
  const rawAlt = ($('Webhook').item.json.body.data.key.remoteJidAlt || '').trim();
  const final = rawJid || rawAlt;

  // Limpa o ID: remove @, espaços, quebras de linha e caracteres especiais
  return final
    .split('@')[0]
    .trim()
    .replace(/\n/g, '')
    .replace(/\r/g, '')
    .replace(/\s/g, '');
})() }}

={{ (() => {
  const jid = ($('Webhook').item.json.body.data.key.remoteJid || '').trim();
  const alt = ($('Webhook').item.json.body.data.key.remoteJidAlt || '').trim();

  // 1. Se o ALT tem número real (começa com 55), usa só os dígitos limpos
  if (alt && alt.startsWith('55')) {
    return alt.split('@')[0].trim().replace(/\n/g, '').replace(/\r/g, '');
  }

  // 2. Se o JID tem número real (começa com 55), usa só os dígitos limpos  
  if (jid && jid.startsWith('55')) {
    return jid.split('@')[0].trim().replace(/\n/g, '').replace(/\r/g, '');
  }

  // 3. Se for LID, retorna o JID COMPLETO (com @lid) mas limpo
  const finalJid = jid || alt || 'SEM_NUMERO';
  return finalJid.trim().replace(/\n/g, '').replace(/\r/g, '');
})() }}

={{ (() => {
  const msgs = $('Apagar Buffer').item.json.messages.join('\n\n');
  const raw = JSON.stringify($('Webhook').first().json);
  const match = raw.match(/"quotedMessage"[\s\S]*?"conversation":\s*"([^"]+)"/);
  const quoted = match ? match[1].replace(/\\n/g, ' ') : '';
  if (quoted && quoted.length > 0) {
    return msgs + '\n\n[CONTEXTO: Cliente respondendo a: "' + quoted + '"]';
  }
  return msgs;
})() }}

